<?phpclass OrderCall extends Module{	private $SendResult = true;		public function __construct(&$db, &$params) 	{		parent::__construct($db, $params);	}			public function LoadBoxContent()	{		if (isset($_POST['order_call']))		{			$secure_img = new Securimage();			if (isset($_POST['captest']))				$valid = $secure_img->check($_POST['captest']);			else $valid = false;						if ($valid)			{				order_call();				$this->tpl->assign('successful', "{#successful#}");			}			else			{				$this->tpl->assign('successful', "{#captcha_error#}");				$this->tpl->assign('fio', $_POST['fio']);				$this->tpl->assign('call_phone', $_POST['call_phone']);				$this->tpl->assign('theme', $_POST['theme']);			}		}		$this->tpl->assign('toaddress', ADMIN_EMAIL);		$this->tpl->assign('action', HOST.substr($_SERVER['REQUEST_URI'],1));		$this->tpl->assign('language', $_SESSION['lang_folder']);		$this->tpl->assign('lang_url', $_SESSION['lang_url']);		$this->tpl->assign('CURRENT_TEMPLATE', CURRENT_TEMPLATE);		$this->tpl->assign('subject', CALLBACK_SUBJECT);		$this->tpl->assign('captcha', get_captcha(DIR_CAPTCHA.'securimage/securimage_show.php'));		}			public function LoadContent(){}}?>